    <div id="navigation">		<ul>			<?php				$i = 0;				$count = count($administration_menu);				$buttons = NULL;				foreach ($administration_menu as $module_name => $menu) {					if (($current_module == $module_name) && isset($administration_menu[$module_name]['buttons'])) {						$buttons = $administration_menu[$module_name]['buttons'];					}                    $i++;	                ?><li class="<?php if (($current_module == $module_name) OR (isset($menu['sub']) && isset($menu['sub'][$current_module]))) echo 'c'; ?> <?php if( $i == 1 ) echo ' f'; else if( $i == $count ) echo ' l'; ?>"><a href="<?php echo $menu['url']; ?>"><?php echo $menu['name']; ?></a><?php	                if (isset($menu['sub']) && !empty($menu['sub'])) {		                ?><ul><?php						foreach ($menu['sub'] as $submodule_name => $submenu) {							if (($current_module == $submodule_name) && isset($menu['sub'][$submodule_name]['buttons'])) {								$buttons = $menu['sub'][$submodule_name]['buttons'];							}							?><li class="<?php if ($current_module == $submodule_name) echo 'c'; ?>"><a href="<?php echo $submenu['url']; ?>"><?php echo $submenu['name']; ?></a></li><?php						 }						?></ul><?php					}					?></li><?php				}			?>		</ul>	</div><div id="main">	<div class="bl"><div class="br"><div class="bb"><div class="bt"><div class="tlc"><div class="trc"><div class="blc"><div class="brc">	<div id="mainHeader">		<h2><?php echo ucfirst(str_replace("_", " ", $current_module)); ?></h2>		<?php			if (isset($buttons)) {				$buttons = array_reverse($buttons, TRUE);                foreach ($buttons as $button_text => $button_url) { ?>					<a style="margin-left: 10px;" href="<?php echo $button_url; ?>"><span><?php echo $button_text; ?></span></a>				<?php }			}		?>	</div>