		<div class="left">all</div>											<input type="checkbox" id="mark_all" onClick="markAll();" />											<div class="clear"></div>										</div><br />									</div>									<div class="clear"></div>								</div>								<div class="tbody">									<script>										$(document).ready(function() {											$('abbr').each(function(){												$(this).qtip({												   content: $(this).attr('name'),												   show: 'mouseover',												   hide: 'mouseout'												});											});										});									</script>									<?php 										if(isset($subpage) && $subpage <> 'backers')											$items = $helpers;										else											$items = $backers;										foreach($items as $pledge) {									?>										<div class="item">											<div class="username">												<a href="/user/<?php echo $pledge->user_slug?>/" title="<?php echo $pledge->username?>">													<?php echo trunc($pledge->username, 8) ?>												</a>											</div> 											<div class="date">												<?php $date = new DateTime($pledge->date_added);  ?>												<abbr name="<?php echo $date->format('d M Y H:s');  ?>">													<?php 														echo $date->format('d/m/Y'); 													?>												</abbr>											</div> 											<div class="pound-pledged">												<abbr name="<?php echo $pledge->pledge_description;  ?>">													&pound;<?php echo h(st($pledge->amount)) ?>												</abbr>											</div> 											<div class="time-pledged">												<?php if($pledge->helper_hours > 0) { ?>													<?php echo h(st($pledge->helper_hours)) ?>												<?php } else { ?>													-												<?php } ?>/												<?php if($pledge->helper_text) { ?>													<abbr name="<?php echo h(st($pledge->helper_text)) ?>">														YES													</abbr>												<?php } else { ?>													-												<?php } ?>											</div> 											<div class="reward-type">								