												<div style="width: 70px;" class="label">Number</div>												<input class="pledge_small focus-style" name="amounts_numbers[<?php echo $amount->idamount?>]" value="<?php echo $amount->number?>" <?php if($amount->used){ echo "DISABLED"; } ?> />											</div>										<div class="clear10"></div>										<div class="label">Description of reward</div>										<textarea class="pledge<?php if(!$amount->description) echo ' error' ?>" name="amounts_descriptions[<?php echo $amount->idamount?>]" <?php if($amount->used){ echo "DISABLED"; } ?>><?php echo $amount->description?></textarea>																				<?php if(!$amount->used){ ?>											<a href="javascript:;" onClick="removeAmount('<?php echo $amount->idamount?>', '<?php echo $amount->idamount?>');" style="margin-left: 210px; padding-top: 5px; float: left;">Remove reward</a>										<?php } else { ?>											<div class="clear5"></div>																						<input type="hidden" name="amounts[<?php echo $amount->idamount?>]" value="<?php echo $amount->amount?>">											<input type="hidden" name="amounts_limited[<?php echo $amount->idamount?>]" value="<?php echo $amount->limited?>">											<input type="hidden" name="amounts_numbers[<?php echo $amount->idamount?>]" value="<?php echo $amount->number?>">											<input type="hidden" name="amounts_descriptions[<?php echo $amount->idamount?>]" value="<?php echo $amount->description?>">																					<center>												The reward is already used and you can't edit it											</center>										<?php } ?>										<div class="clear10"></div>									</div>								<?php } ?>							<?php } ?>						<?php } elseif(!empty($_POST['amounts'])){ // When we add a project ?>													<?php foreach($_POST['amounts'] AS $k => $amount){ ?>								<div class="pledge focus-style" id="amount_<?php echo $k?>">									<span class="title">REWARD </span>									<div class="clear10"></div>									<div style="line-height: normal;" class="label">										<?php if(!$_POST['amounts'][$k] OR !is_numeric($_POST['amounts'][$k])) echo '<b style="color: red;">*</b>' ?>										Pledge amount <small>(You can only enter numbers)</small>									</div>									<input maxlength="6" class="pledge" name="amounts[<?php echo $k?>]" value="<?php echo $_POST['amounts'][$k]?>" />									<div class="clear10"></div>										<div style="line-height: normal;" class="label">Are there a limited number<br /> of this reward available?</div>										<select name="amounts_limited[<?php echo $k?>]" class="pledge focus-style">											<option value="no" <?php if($_POST['amounts_limited'][$k] == "no"){ echo "SELECTED"; } ?>>No</option>											<option value="yes" <?php if($_POST['amounts_limited'][$k] == "yes"){ echo "SELECTED"; } ?>>Yes</option>										</select>										<div class="n_reward_l"<?php if($_POST['amounts_limited'][$k] == "yes"){ echo ' style="display: block;"'; } ?>>											<div style="width: 70px;" class="label">												<?php if($_POST['amounts_limited'][$k] == "yes" && !is_numeric($_POST['amounts_numbers'][$k])) echo '<b style="color: red;">*</b>' ?>												Number											</div>											<input class="pledge_small focus-style" name="amounts_numbers[<?php echo $k?>]" value="<?php echo $_POST['amounts_numbers'][$k]?>" />										</div>									<div class="clear10"></div>									<div class="label">										<?php if(!$_POST['amounts_descriptions'][$k]) echo '<b style="color: red;">*</b>' ?>										Description of reward									</div>									<textarea class="pledge" name="amounts_descriptions[<?php echo $k?>]"><?php echo $_POST['amounts_descriptions'][$k]?></textarea>									<div class="clear10"></div>								</div>								<div class="clear10"></div>							<?php } ?>						<?php } else { // It's just a blank when no amounts are found ?>							<div class="pledge focus-style" id="amount_0">								<span class="title">REWARD </span>								<div class="clear10"></div>								<div style="line-height: normal;" class="label">Pledge amount <small>(You can only enter numbers)</small></div><input maxlength="6" class="pledge focus-style" name="amounts[0]" value="" /><div class="clear10"></div>									<div style="line-height: normal;" class="label">Are there a limited number<br /> of this reward available?</div>									<select name="amounts_limited[0]" class="pledge focus-style">										<option value="no">No</option>										<option value="yes">Yes</option>									</select>									<div class="n_reward_l">										<div style="width: 70px;" class="label">Number</div>										<input class="pledge_small focus-style" name="amounts_numbers[0]" value="" />									</div>								<div class="clear10"></div>								<div class="label">Description of reward</div>								<textarea class="pledge focus-style" name="amounts_descriptions[0]"></textarea>								<div class="clear10"></div>							</div>						<?php } ?>					</div>										<div class="clear10"></div>					<input class="button left" name="button" type="button" value="Add another" onClick="addAmount();" /><div class="clear10"></div>				</fieldset>									<script>					$(document).ready(function(){						$('.global_form_2 div.pledge select.pledge').change(function(){							if($(this).val() == 'yes') 								$(this).parent().find('.n_reward_l').show();							else								$(this).parent().find('.n_reward_l').hide();						});					});					function addAmount(){						var cnt = parseInt($("#amountsCount").val());						if(cnt > 11) return false;						cnt = cnt + 1;						$("#amountsCount").val(cnt);												var html = '';						html += '<div class="pledge focus-style" id="amount_'+cnt+'" style="margin-top: 10px;">';						html += '<span class="title">REWARD </span>';						html += '<div class="clear10"></div>';						html += '<div style="line-height: normal;" class="label">Pledge amount <small>(You can only enter numbers)</small></div><input maxlength="6" class="pledge focus-style" name="amounts['+cnt+']" value="" /><div class="clear10"></div>';						html += '<div style="line-height: normal;" class="label">Are there a limited number<br /> of this reward available?</div>';						html += '<select name="amounts_limited['+cnt+']" class="pledge focus-style">';						html += '<option value="no">No</option>';						html += '<option value="yes">Yes</option>';						html += '</select>';						html += '<div class="n_reward_l">';						html += '<div style="width: 70px;" class="label">Number</div>';						html += '<input class="pledge_small focus-style" name="amounts_numbers['+cnt+'] "value="" />';						html += '</div>';						html += '<div class="clear10"></div>';						html += '<div class="label">Description of reward</div>';						html += '<textarea class="pledge focus-style" name="amounts_descriptions['+cnt+']"></textarea>';						html += '<a href="javascript:;" onClick="removeAmount('+cnt+', 0);" style="margin-left: 210px; padding-top: 5px; float: left;">Remove reward</a>';						html += '<div class="clear10"></div>';						html += '</div>';													$("#amount_options_container").append(html);												$('.global_form_2 div.pledge select.pledge').change(function(){							if($(this).val() == 'yes') 								$(this).parent().find('.n_reward_l').show();							else								$(this).parent().find('.n_reward_l').hide();						});												changed = true;					}										function removeAmount(idelement, idamount){						$("#amount_"+idelement).remove();						if(idamount)							$(".global_form_2").append("<input type='hidden' name='remove_amount[]' value='"+idamount+"'>");						changed = true;					}					function removeAddedAmount(id){						$("#added_amount_"+id).hide();						$("#added_delete_"+id).val("1");												changed = true;					}				</script>