		<meta property="og:url" content="http://<?php echo $_SERVER["HTTP_HOST"].$_SERVER['REQUEST_URI'] ?>" /> 		<meta property="og:site_name" content="People Fund" /> 			<meta property="og:title" content="<?php 			if (isset($fb_title) && !empty($fb_title)) { 				echo h(st($fb_title)) ;			} else {				if(isset($page_title) && $page_title) 					echo h(st($page_title)) ;			}		?>" />		<meta property="og:description" content="<?php 			if (isset($fb_description) && !empty($fb_description)) {				echo h(st($fb_description)) ;			} else {				echo 'On peoplefund.it you can find good ideas that might just change the world. Then you can help make them happen with you spare cash, in return for your choice of reward. It’s quick to back a project, and your money will only leave your account if the idea you support raises its target. If you really like an idea you can also support it with your time and skills – and by telling your friends!';			}		?>" />		<?php 			if (isset($project) && !empty($project)) {				// Uploaded vzaar video				if((int)$project->vzaar_idvideo > 0 && (int)$project->vzaar_processed > 0) {		?>							<link rel="video_src" href="https://view.vzaar.com/<?php echo (int)$project->vzaar_idvideo ?>.flashplayer" />					<link rel="image_src" href="https://view.vzaar.com/<?php echo (int)$project->vzaar_idvideo ?>.thumb">					<meta name="medium" content="video" />					<meta property="og:type" content="movie" />					<meta property="og:video" content="https://view.vzaar.com/<?php echo (int)$project->vzaar_idvideo ?>.flashplayer"/> 					<meta property="og:image" content="https://view.vzaar.com/<?php echo (int)$project->vzaar_idvideo ?>.thumb"/> 					<meta name="video_width" content="590" />					<meta name="video_height" content="338" /> 					<meta name="video_type" content="application/x-shockwave-flash" />			<?php } else if (!empty($project->embed)) { 					// Embeded videos					$video_url = '';					// Iframe embed code					if (preg_match('/iframe/', $project->embed) > 0) {						preg_match('/src="[a-zA-Z0-9&\/\.?=_\-:]+"/', $project->embed, $matches); // matching the video url					if(!empty($matches[0]))						$video_url = substr($matches[0], 5, (strlen($matches[0]) - 6)); // removing other than the url string					// Old embed code					} elseif (preg_match('/object/', $project->embed) > 0) {						preg_match('/name="movie" value="[a-zA-Z0-9&\/\.?=_\-:]+"{1}/', $project->embed, $matches); // matching the video url						if(!empty($matches[0]))							$video_url = substr($matches[0], 20, (strlen($matches[0]) - 21)); // removing other than the url string					}					if(!empty($video_url)) {  						// Detect vimeo clips						if (preg_match('/vimeo.com/', $video_url) > 0){							preg_match('/[0-9]{1,50}/', $video_url, $matches); // matching the video id							if((int) $matches[0] > 0){								$video_url = 'http://vimeo.com/moogaloop.swf?clip_id='.$matches[0];							?>						<meta property="og:video:type" content="application/x-shockwave-flash" />							<?php }						} elseif (preg_match('/youtube.com/', $video_url) > 0){							$video_url = str_replace(array('/embed/'), array('/v/'), $video_url) . '?version=3&feature=oembed';						}					?>						<meta property="og:video" content="<?php echo $video_url; ?>" />						<meta property="og:type" content="movie" />					<?php  } }				if (!empty($project->ext)){ ?>					<meta property="og:image" content="<?php echo site_url('/uploads/projects/'.h(st($project->idproject)).'.'.h(st($project->ext))) ?>" />			<?php } else { ?>					<meta property="og:image" content="<?php echo site_url('/share/images/site/logo-fb.jpg') ?>"/>			<?php } 		} else  { 			// Detect vzaar video on page content			preg_match('/data="http(s):\/\/view.vzaar.com\/([0-9]{6,50}).flashplayer"/is', @$page->body, $matches);			$matches = isset($matches[2]) && is_numeric($matches[2]) ? $matches[2] : FALSE;						if(!$matches) {				preg_match('/src="http([a-z]{0,1}):\/\/view.vzaar.com\/([0-9]{6,50})\/player"/is', @$page->body, $matches);				$matches = isset($matches[2]) && is_numeric($matches[2]) ? $matches[2] : FALSE; 			}			if($matches) { ?>			<meta property="og:video" content="http://view.vzaar.com/<?php echo (int) $matches ?>.flashplayer" /> 			<meta property="og:image" content="http://view.vzaar.com/<?php echo (int) $matches ?>.thumb" /> 		<?php } else { ?>			<meta property="og:image" content="<?php echo site_url('/img/site/logo-fb.jpg') ?>"/>		<?php } } ?>